import React, { useState } from "react";

const handleSubmit = (e) => {
  e.preventDefault();
  if (!formData.campaignName || !formData.targetWallet || !formData.budget || !formData.message) {
    alert("All fields are required!");
    return;
  }
  onAddCampaign(formData);
  setFormData({
    campaignName: "",
    targetWallet: "",
    budget: "",
    message: "",
  });
  alert("Campaign Created Successfully!");
};


const CampaignForm = ({ onAddCampaign }) => {
  const [formData, setFormData] = useState({
    campaignName: "",
    targetWallet: "",
    budget: "",
    message: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddCampaign(formData); // Pass the campaign data to the parent
    setFormData({
      campaignName: "",
      targetWallet: "",
      budget: "",
      message: "",
    });
    alert("Campaign Created Successfully!");
  };

  return (
    <div>
      <h2>Create a Campaign</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Campaign Name:</label>
          <input
            type="text"
            name="campaignName"
            value={formData.campaignName}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Target Wallet:</label>
          <input
            type="text"
            name="targetWallet"
            value={formData.targetWallet}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Budget (in Crypto):</label>
          <input
            type="number"
            name="budget"
            value={formData.budget}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Message:</label>
          <textarea
            name="message"
            value={formData.message}
            onChange={handleChange}
          ></textarea>
        </div>
        <button type="submit">Create Campaign</button>
      </form>
    </div>
  );
};

export default CampaignForm;
